  from dgn_importexcelconf_s where FIND_IN_SET(TableColName,varDataPK);
 
 
 -- 更新语句
   select GROUP_CONCAT(  concat('b.',b.tablecolname ,'='
  ,  case when b.IsJoin =1 then 
   concat('ifnull((',b.JoinTableSql  ,'),',b.excelcolname,')')
  else  concat('a.`',b.excelcolname,'`') end
  ))  into varColUpdateStr
  from   dgn_importexcelconf_s b 
  where  b.formid=varFormID and b.IsValid=1 and b.IsAllowUpdate=1;
    
  set  varUpdateSqlStr=concat('update  `',varExcelTableName,'` a ,',varTableName,' b  set ',varColUpdateStr,' where ',varDataPKjoin);
 
 --  插入语句  excel列名
    select GROUP_CONCAT(concat(case when b.IsJoin =1 then 
   concat('ifnull((',b.JoinTableSql , '),',ifnull(b.defaultvalue,b.excelcolname),') ',b.excelcolname)
  when ifnull(b.excelcolname,'')='' then  b.defaultvalue 
  else  concat('ifnull(a.`',b.excelcolname,'`,',ifnull(b.defaultvalue,b.excelcolname),')') end
  ))  into varColInsertStr1
  from   dgn_importexcelconf_s b 
  where  b.formid=varFormID and b.IsValid=1 and b.IsAllowInsert=1
  order by b.tablecolname;
  
  --  插入语句  表列名
    select GROUP_CONCAT(b.tablecolname)  into varColInsertStr
  from   dgn_importexcelconf_s b 
  where  b.formid=varFormID and b.IsValid=1 and b.IsAllowInsert=1
   order by b.tablecolname;
  
  set varInsertSqlStr=concat('insert into ',varTableName,'(',varColInsertStr,')',' select ',varColInsertStr1,' from `',varExcelTableName,'` a left join ',varTableName,' b on ',varDataPKjoin,' where b.id is null' );
 
   set @varSqlStr=varUpdateSqlStr ;
         PREPARE psql FROM @varSqlStr;  
		 EXECUTE psql ;  
   set @varSqlStr=varInsertSqlStr ;
         PREPARE psql1 FROM @varSqlStr;  
		 EXECUTE psql1 ;  
   set @varSqlStr=concat('drop table `',varExcelTableName,'`' );
         PREPARE psql2 FROM @varSqlStr;  
		 EXECUTE psql2 ;    
   
END ;;
