CREATE DEFINER=`root`@`localhost` PROCEDURE `dgn_UserLogin`(varAppID varchar(50),varLoginID varchar(50),varLoginType tinyint,varUserType tinyint,varPassWord varchar(50))
BEGIN
    -- CALL  `dgn_UserLogin`('1000','friuiowqueoikdsjkwoieuo','15618551880',2, 1,  'iipoi9887878798');
	declare varPassWordHash varchar(200);
    declare vardbuserid bigint;
    declare vardbpasswordhash varchar(200);
	declare vardbstartdate date;
    declare vardbendstartdate date;
	declare varAccessToken varchar(50);
    
    if not exists ( select 1 from dgn_appid a where a.AppID=varAppID) then
		select 'fail' as result,'登录失败,请确认登录配置参数是否正确。' resultDescribe ;
	else
        SELECT  b.userid,b.password,startdate,enddate into vardbuserid,vardbpasswordhash,vardbstartdate,vardbendstartdate
			FROM  dgn_login_type a inner join dgn_user b on a.userid=b.userid   
			where  a.LoginID=varLoginID and a.LoginType=varLoginType and b.usertype=varUserType
			and b.isCancel=0 and b.isAllowLogon=1  limit 1 offset  0 ;
		if vardbuserid is null then
			select 'fail' as result,'登录失败,请确认用户名与密码是否正确。' resultDescribe ;
		else
			set varPassWordHash=concat(varPassWord,vardbuserid);
			set varPassWordHash=sha1(varPassWordHash);
			if varPassWordHash=vardbpasswordhash then
				if curdate()>=vardbstartdate and curdate()<=vardbendstartdate  then
					delete from `dgn_access_token`  where  `dgn_access_token`.UseriD=vardbuserid and `dgn_access_token`.appid=varAppID ;
                    set varAccessToken=uuid();
					insert into `dgn_access_token`  set `dgn_access_token`.AppID=varAppID,`dgn_access_token`.UseriD=vardbuserid,`dgn_access_token`.AccessToken=varAccessToken,ExpireTime=date_add(now(), interval 30 day);
				    insert into `dgn_log` set `dgn_log`.AppID=varAppID,UseriD=vardbuserid ,Module='登录',Operation='用户登录成功',`describe`= concat(varLoginID,varLoginType,varUserType),LoginID=varLoginID; 
					select 'success' as result,'登录成功' resultDescribe,varAccessToken as accessToken,vardbuserid as userID ;
					if  varusertype=1 then  -- 公司人员表取人员信息
						select *,varLoginID as LoginID from dgn_person where userid=vardbuserid;
					else 
						insert into `dgn_log` set `dgn_log`.AppID=varAppID,UseriD=vardbuserid ,Module='登录',Operation=concat('不支持用户类型:',varUserType),`describe`=concat(varLoginID,varLoginType,varUserType),LoginID=varLoginID; 
						select 'fail' as result,concat('不支持用户类型:',varUserType) resultDescribe ;
					end if;
				else
					insert into `dgn_log` set `dgn_log`.AppID=varAppID,UseriD=vardbuserid, Module='登录',Operation='不在帐号有效期,请联系管理员。',`describe`=concat(varLoginID,varLoginType,varUserType),LoginID=varLoginID; 
					select 'fail' as result,'不在帐号有效期,请联系管理员。' resultDescribe ;
				end if;
			else
				select 'fail' as result,'登录失败,请确认用户名与密码是否正确。' resultDescribe ;
			end if;
		end if ;
	end if;
END ;;
