CREATE DEFINER=`root`@`localhost` PROCEDURE `dgn_UserReg`(varLoginID varchar(50),varNickName varchar(50),varLoginType tinyint,varUserType tinyint,varPassWord varchar(50),varValidateCode varchar(50))
label_exit: BEGIN
-- LoginType
-- 1:用户ID 登陆         默认
-- 6:用户code 登陆
-- 2:手机
-- 3:Email
-- 4:qq
-- 5:微信
-- usertype
-- 1:公司人员            默认
-- 2:会员
-- CALL dgn_UserReg('15618551880','Gavin',2,1,'50f06df1a1a6edd27d7e296fb1b726760657096a','908767')  ;
-- CALL dgn_UserReg('abc@dgn.com','dgn',3,1,'50f06df1a1a6edd27d7e296fb1b726760657096a','0')  ;
    declare varUserID bigint;
    declare varPassWordHash varchar(200);
    declare varDbValidateCode varchar(50);
	DECLARE var_error INTEGER DEFAULT 0;  
	DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET var_error=1;  
   
   if  exists
		(SELECT 1 FROM  dgn_login_type a inner join dgn_user b on a.userid=b.userid   
			where  a.LoginID=varLoginID  and b.usertype=varUserType  
        )  then
      --  insert into `dgn_log` set UseriD=UserID,CreateTime=curdate(),Module='登录',Operation='该账号已注册',`describe`=concat(LoginID,LoginType,UserType); 
		select 'fail' as result,'该账号已注册' resultDescribe;
        leave  label_exit;
    end if ;
        
   if    varValidateCode='nocheck' then   -- 验证码为nocheck则认为不需要验证
		set varValidateCode='';
   else
	  select ValidateCode into varDbValidateCode from `dgn_validate_code` where loginid=varLoginID and LoginType=varLoginType and usertype=varUserType and  now()<=ExpireTime ;
      if  varValidateCode =varDbValidateCode 
        then    -- 验证码正确则删除
			delete from `dgn_validate_code` where loginid=varLoginID and LoginType=varLoginType and usertype=varUserType ;
		else 
			select 'fail' as result,'验证码错误' resultDescribe;
			leave  label_exit;
        end if ;
   end if;
   
    set varUserID=uuid_short();
    set varPassWordHash=concat(varPassWord,varUserID);
    set varPassWordHash=sha1(varPassWordHash);
    start transaction  ;
		insert into `dgn_person` set UserID=varUserID,Code=varLoginID,Name=varNickName,Mobile=case when varLoginType=2 then varLoginID else '' end ,Email=case when varLoginType=3 then varLoginID else '' end,CreateTime=now(),UserImages=uuid_short();
		insert into `dgn_user` set UseriD=varUserID,PassWord=varPassWordHash,UserType=varUserType,StartDate=curdate(),EndDate=date_add(curdate(), interval 3650 day),IsAllowLogon=1,IsCancel=0,CreateTime=now();
        insert into `dgn_login_type` set UserID=varUserID,LoginID=varUserID,NickName=varNickName,LoginType=1;
        insert into `dgn_login_type` set UserID=varUserID,LoginID=varLoginID,NickName=varNickName,LoginType=varLoginType;
        insert into `dgn_log` set UseriD=varUserID,LoginID=varLoginID,CreateTime=now(),Module='登录',Operation='注册成功',`describe`=concat(varLoginID,varLoginType,varUserType); 
    IF var_error = 1 THEN  
        ROLLBACK;  
        insert into `dgn_log` set UseriD=0,LoginID=varLoginID,CreateTime=now(),Module='登录',Operation='用户注册出现错误',`describe`=concat(varLoginID,varLoginType,varUserType); 
        select 'fail' as result,'用户注册出现错误' resultDescribe;
        leave  label_exit;
	else  
		COMMIT;  
        select 'success' as result,'注册成功' resultDescribe;
        leave  label_exit;
	END IF;  

END ;;
